import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Row, Col, Icon, Button, Input } from 'antd'

import { sendChatMsg } from '../../../redux/actions'
import { sendMsg } from '../../../utils/websocket'
import Face from '../../../components/message/face'

class ChatTextarea extends Component {

    state = { message: '', showFace: false }

    componentDidMount(){
        document.onclick = this.onblur
    }

    onblur =() => {
        this.setState({
            showFace:false
        })
    }

    handleTextArea = (name, e) => {
        this.setState({ [name]: e.target.value })
    }

    render() {
        const { showFace } = this.state
        return (
            <section style={{ position: "relative" }}>
                <Row className="chat-tools">
                    <Col span={18}>
                        <Icon type="smile" onClick={this.showFace} style={{ marginLeft: 5, marginRight: 10 }} />
                        <Icon type="folder" />
                        <Face showFace={showFace} />
                    </Col>
                    <Col span={6} style={{ textAlign: "right" }}>
                        <Icon type="video-camera" style={{ marginRight: 10 }} />
                        <Icon type="download" />
                    </Col>
                </Row>
                <Input.TextArea className="chat-textarea"
                       onChange={e => this.handleTextArea('message', e) } placeholder="输入信息..."
                       onPressEnter={e => this.sendChatMess(e) }
                       value={this.state.message}
                />
                <Button style={{ float: "right" }} onClick={ e => this.sendChatMess(e) }>发送</Button>
            </section>
        )
    }

    showFace = () => {
        e.nativeEvent.stopImmediatePropagation()
        const {showFace} = this.state
        this.setState({
            showFace: !showFace
        })
    }

    sendChatMess = e => {
        e.preventDefault();
        const { message } = this.state
        if(!message) return
        this.setState({ message: '' })
        const { user } = this.props
        const msgData = {
            id: user.user_id,
            avatar: user.avatar,
            message: message,
            position: "right"
        }
        sendMsg(message)
        this.props.sendChatMsg(msgData)
    }
}

export default connect(
    state => ({ chat: state.chat, user: state.user }),
    { sendChatMsg }
)(ChatTextarea)
