import React, { Component } from 'react'
import { Avatar, Row, Col, Skeleton } from 'antd'

export default class Message extends Component {

    state = {
        loading: true,
        list: []
    }

    componentDidMount() {
        this.getMessList((messList) => {
            setTimeout(() => {
                this.setState({
                    loading: false,
                    list: messList
                })
            }, 1000)
        })
    }

    getMessList = (callback) => {
        const messList = [
            {avatar: "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778785114&di=4bf0a9cbe00cd200465af9fe46b3091e&imgtype=0&src=http%3A%2F%2Fwww.56ec.org.cn%2Fd%2Ffile%2Fnews%2Frwgd%2F2017-05-15%2Fa26c40ec7d83c66d78ad6f791952a01b.jpg", message: "下个月花呗不用还了，我还要另外支付你一个亿随便你花", position: "left"},
            {avatar: "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778785114&di=4bf0a9cbe00cd200465af9fe46b3091e&imgtype=0&src=http%3A%2F%2Fwww.56ec.org.cn%2Fd%2Ffile%2Fnews%2Frwgd%2F2017-05-15%2Fa26c40ec7d83c66d78ad6f791952a01b.jpg", message: "下个月花呗不用还了，我还要另外支付你一个亿随便你花", position: "left"},
            {avatar: "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778785114&di=4bf0a9cbe00cd200465af9fe46b3091e&imgtype=0&src=http%3A%2F%2Fwww.56ec.org.cn%2Fd%2Ffile%2Fnews%2Frwgd%2F2017-05-15%2Fa26c40ec7d83c66d78ad6f791952a01b.jpg", message: "下个月花呗不用还了，我还要另外支付你一个亿随便你花", position: "left"},
            {avatar: "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778785114&di=4bf0a9cbe00cd200465af9fe46b3091e&imgtype=0&src=http%3A%2F%2Fwww.56ec.org.cn%2Fd%2Ffile%2Fnews%2Frwgd%2F2017-05-15%2Fa26c40ec7d83c66d78ad6f791952a01b.jpg", message: "下个月花呗不用还了，我还要另外支付你一个亿随便你花", position: "left"},
            {avatar: "http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134", message: "感谢马云大大，祝你生意越来越好", position: "right"},
            {avatar: "http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134", message: "感谢马云大大，祝你生意越来越好", position: "right"},
            {avatar: "http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134", message: "感谢马云大大，祝你生意越来越好", position: "right"},
        ]
        return callback(messList)
    }

    render() {
        const { loading } = this.state
        return (
            <div>
                <Skeleton loading={loading} avatar={{ shape: "square" }} active paragraph={{ rows: 6 }}>
                    <div className="message-item message-left">
                        <Row>
                            <Col span={1}>
                                <Avatar shape="square" src="" />
                            </Col>
                            <Col span={23}>
                                <div className="message">
                                    <span>随便你花</span>
                                </div>
                            </Col>
                        </Row>
                    </div>

                    <div className="message-item message-right">
                        <Row>
                            <Col span={1} push={23} style={{ textAlign: "right" }}>
                                <Avatar shape="square" src="" />
                            </Col>
                            <Col span={23} pull={1}>
                                <div className="message">
                                    <span>随便你花下个月花呗不用还了，我还要另外支付你一个亿随便你花</span>
                                </div>
                            </Col>
                        </Row>
                    </div>
                </Skeleton>
            </div>
        )
    }
}
