/**
 * 对应 reducers 中的 action 同步/异步请求
 */
import { INIT_CHAT_INFO, INIT_MESS_LIST } from './action-type'

const chatData = (chat) => ({ type: INIT_CHAT_INFO, data: chat })
const messList = (messList) => ({ type: INIT_MESS_LIST, data: messList })

export const initChatInfo = (userInfo) => {
    return dispatch => {
        dispatch(chatData({
            chatUserInfo: userInfo
        }))
    }
}

export const initMessList = () => {
    return dispatch => {

        dispatch(messList())
    }
}
