import React, { Component } from 'react'
import { Row, Col, Icon, Button, Input, message as AntdMessage } from 'antd'

export default class ChatTextarea extends Component {

    state = {
        message: ''
    }

    handleTextArea = (name, e) => {
        this.setState({
            [name]: e.target.value
        })
    }

    sendChatMess = () => {
        const { message } = this.state
        if(!message) {
            AntdMessage.error('不能发送空字符')
            return
        }
        AntdMessage.success(`本次发送的数据为: ${message}`)
    }

    render() {
        return (
            <div>
                <div className="chat-tools">
                    <Row>
                        <Col span={18}>
                            <Icon type="smile" style={{ marginLeft: 5, marginRight: 10 }} />
                            <Icon type="folder" />
                        </Col>
                        <Col span={6} style={{ textAlign: "right" }}>
                            <Icon type="video-camera" style={{ marginRight: 10 }} />
                            <Icon type="download" />
                        </Col>
                    </Row>
                </div>
                <Input.TextArea className="chat-textarea"
                       onChange={e => { this.handleTextArea('message', e) }} placeholder="输入信息..."
                       onPressEnter={() => { this.sendChatMess() }}
                />
                <Button style={{ float: "right" }} onClick={ () => { this.sendChatMess() }}>Enter</Button>
            </div>
        )
    }
}
