import React, { Component } from 'react'
import { Layout } from 'antd'

import Message from './struct/message'
import MessHeader from './struct/header'
import ChatTextarea from './struct/textarea'

import LayoutMain from '../../../components/layout'

const { Header, Content, Footer } = Layout
export default class ChatMessage extends Component {
    render() {
        return (
            <LayoutMain router={ { path: '/', components: this.createComponents() } } />
        )
    }

    createComponents = () => {
        return (
            <div style={{ maxHeight: "100%", minHeight: "100%" }}>
                <Header className="message-header"><MessHeader /></Header>
                <Content className="message-body"><Message /></Content>
                <Footer className="message-footer"><ChatTextarea /></Footer>
            </div>
        )
    }
}
