import { combineReducers } from 'redux'

import { INIT_CHAT_INFO } from './action-type'

const initUser = {
    user_id: 1,
    nickname: 'Mr.bo',
    avatar: 'http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134'
}
function user(state = initUser, action) {
    return state
}

const initChatInfo = {
    messList: [],
    chatUserInfo: {
        uid: 0,
        avatar: '',
        name: ''
    }
}
function chat(state = initChatInfo, action) {
    switch (action.type) {
        case INIT_CHAT_INFO:
            return {...action.data}
        default:
            return state
    }
}

export default combineReducers({
    user,
    chat
})
