import React, {Component} from 'react'
import {withRouter} from 'react-router-dom'
import {connect} from 'react-redux'
import {Input, List, Avatar} from 'antd'

class NewFriend extends Component {

    state = {
        data: [
            {
                title: 'Ant Design Title 1',
            },
            {
                title: 'Ant Design Title 2',
            },
            {
                title: 'Ant Design Title 3',
            },
            {
                title: 'Ant Design Title 4',
            },
        ]
    }

    render() {
        const { data } = this.state
        return (
            <div className="m-user-info">
                <h2 className="main-title">添加好友</h2>
                <div className="friend-list">
                    <Input.Search placeholder="好友ID/昵称/手机号" enterButton="查找"
                        size="large" onSearch={value => console.log(value)}
                    />
                    <List className="m-list-user" itemLayout="horizontal" dataSource={data} split={false}
                          renderItem={user => (
                              <List.Item>
                                  <List.Item.Meta
                                      avatar={<Avatar shape="square" size="large" src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                                      title={<a href="https://ant.design">{item.title}</a>}
                                  />
                              </List.Item>
                          )}
                    />
                    <List
                        itemLayout="horizontal"
                        dataSource={data}
                        renderItem={item => (
                            <List.Item>
                                <List.Item.Meta
                                    avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                                    title={<a href="https://ant.design">{item.title}</a>}
                                    description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                                />
                            </List.Item>
                        )}
                    />
                </div>
            </div>
        )
    }
}

export default connect(
    state => ({friend: state.friend, chat: state.chat}), {}
)(withRouter(NewFriend))
