import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Avatar, Row, Col, Skeleton } from 'antd'

class Message extends Component {

    componentDidUpdate() {
        this.scrollToBottom()
    }

    scrollToBottom = () => {
        this.messagesEnd.scrollIntoView({ behavior: 'auto' });
    }

    render() {
        const { chat: { messList, loading } } = this.props
        return (
            <div>
                <Skeleton loading={loading} avatar={{ shape: "square" }} active paragraph={{ rows: 6 }}>
                    {
                        messList.map(item => {
                            if(item.position === "left") {
                                return (
                                    <div key={item.id} className="message-item message-left">
                                        <Row>
                                            <Col span={1}>
                                                <Avatar shape="square" src={item.avatar} />
                                            </Col>
                                            <Col span={23}>
                                                <div className="message">
                                                    <span>{item.message}</span>
                                                </div>
                                            </Col>
                                        </Row>
                                    </div>
                                )
                            } else {
                                return (
                                    <div key={item.id} className="message-item message-right">
                                        <Row>
                                            <Col span={1} push={23} style={{ textAlign: "right" }}>
                                                <Avatar shape="square" src={item.avatar} />
                                            </Col>
                                            <Col span={23} pull={1}>
                                                <div className="message">
                                                    <span>{item.message}</span>
                                                </div>
                                            </Col>
                                        </Row>
                                    </div>
                                )
                            }
                        })
                    }
                </Skeleton>
                <div ref={(el) => { this.messagesEnd = el }} />
            </div>
        )
    }
}

export default connect(
    state => ({ chat: state.chat }),
    {  }
)(Message)
