import React, {Component} from 'react'
import {withRouter} from 'react-router-dom'
import {connect} from 'react-redux'
import {Avatar, List, Button} from 'antd'
import mailList from '../../config/mailListConfig'

class NewFriend extends Component {

    state = {user_id: 0}

    render() {
        return (
            <div className="m-user-info">
                <h2 className="main-title">好友申请</h2>
                <div className="friend-list">{this.createMailListDom()}</div>
            </div>
        )
    }

    createMailListDom = () => {
        const {user_id} = this.state
        return Object.keys(mailList).map(key => (
            <div className="m-list-item" key={key}>
                <List
                    className="m-list-user"
                    itemLayout="horizontal"
                    dataSource={mailList[key]}
                    split={true}
                    renderItem={user => (
                        <List.Item
                            className={user_id === user.uid ? 'active' : null}
                            onClick={() => this.showUserInfo(user.uid)}
                            actions={[<Button type="primary">接受</Button>, <a key="list-loadmore-more">拒绝</a>]}
                        >
                            <List.Item.Meta
                                avatar={<Avatar shape="square" size="large" style={{marginTop: 5}} src={user.avatar}/>}
                                title={`${user.remark} (${user.name})`}
                                description="老朋友，好久不见啊"
                            />
                        </List.Item>
                    )}
                />
            </div>
        ))
    }
}

export default connect(
    state => ({friend: state.friend, chat: state.chat}), {}
)(withRouter(NewFriend))
