import React, { Component } from 'react'
import { Avatar, Button } from 'antd'
import PubSub from 'pubsub-js'

export default class User extends Component {

    state = { user: null }

    componentDidMount() {
        PubSub.subscribe('showUserInfo', (msg, user) => this.setState({ user }))
        console.log(this.state.user)
    }

    render() {
        const { user } = this.state
        if (!user) return null
        return (
            <div className="m-user-info">
                <div className="user-info-main">
                    <div className="basic-info border-b">
                        <div className="left">
                            <Avatar size={60} shape="square" src={user.avatar} />
                        </div>
                        <div className="right">
                            <div className="user-name">{user.remark}</div>
                            <div className="user-desc">昵称：{user.name}</div>
                        </div>
                        <div className="clearfix" />
                    </div>
                    { this.itemListDom }
                    <div className="btn-operation">
                        <Button type="danger" block={true} onClick={() => this.logout()}>退出登录</Button>
                        <Button block={true}>注销账号</Button>
                    </div>
                </div>
            </div>
        )
    }

    getItemList = () => {
        const itemList = [
            { itemName: "地区", itemValue: "江苏 淮安" },
            { itemName: "昵称", itemValue: "Mr.bo" },
            { itemName: "手机号", itemValue: "183***8888" },
            { itemName: "个性签名", itemValue: "我就是帅，你能有什么办法呢" },
        ]
        return itemList.map((item, index) => (
            <Row className="user-item border-b" key={index}>
                <Col span={4}>{item.itemName}</Col>
                <Col span={20} style={{ textAlign: "right" }}>{item.itemValue}</Col>
            </Row>
        ))
    }
}
