import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Switch, Route } from 'react-router-dom'
import { Layout } from 'antd'

import { changeRightType } from '../../redux/actions'
import Contacts from './contacts'
import MailList from '../mail-list'
import Chat from './message'

const { Sider } = Layout

class ChatContainer extends Component {

    componentWillMount() {
        this.routerDom = this.createSiderDom()
    }

    componentDidMount() {
        const { chat: { chatUserInfo: { uid } } } = this.props
        const type = uid ? 'message' : 'welcome'
        this.props.changeRightType(type)
    }

    render() {
        return (
            <Layout>
                <Sider className="contacts-list" defaultCollapsed="true" collapsedWidth="251" theme="light">
                    <Switch>{ this.routerDom }</Switch>
                </Sider>
                <Layout className="message-container">
                    <Chat />
                </Layout>
            </Layout>
        )
    }

    createSiderDom = () => {
        const routers = [
            { path: '/', components: Contacts },
            { path: '/mail-list', components: MailList },
        ]
        return routers.map(item => (
            <Route exact key={item.path} path={item.path} component={item.components} />
        ))
    }
}

export default connect(
    state => ({ chat: state.chat }), {changeRightType}
)(ChatContainer)
