import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Layout } from 'antd'

import Message from './message'
import MessHeader from './message/header'
import Welcome from '../../components/welcome'
import ChatTextarea from './message/textarea'

import { initChatInfo } from '../../redux/actions'

const { Header, Content, Footer } = Layout
class Chat extends Component {
    render() {
        const { chat: { chatUserInfo: { uid } } } = this.props
        if(!uid) {
            return <Welcome />
        }
        return (
            <div>
                <Header className="message-header"><MessHeader /></Header>
                <Content className="message-body"><Message /></Content>
                <Footer className="message-footer"><ChatTextarea /></Footer>
            </div>
        )
    }
}

export default connect(
    state => ({ chat: state.chat }),
    {initChatInfo}
)(Chat)
