import React, { Component } from 'react'
import { List, Skeleton, Avatar } from 'antd'
import InputSearch from '../search'

export default class Contacts extends Component {

    state = {
        initLoading: true,
        loading: false,
        list: [],
    }

    componentDidMount() {
        this.getData(res => {
            this.setState({
                initLoading: false,
                list: res,
            });
        });
    }

    getData = callback => {
        const res = [
            { "title":"Mr", "first":"Tristan", "last":"Roy" },
            { "title":"Mr", "first":"Veeti", "last":"Ahonen" },
            { "title":"Mr", "first":"Veeti", "last":"Ahonen" },
            { "title":"Mr", "first":"Veeti", "last":"Ahonen" },
            { "title":"Mr", "first":"Veeti", "last":"Ahonen" },
            { "title":"Mr", "first":"Veeti", "last":"Ahonen" },
            { "title":"Mrs", "first":"Marilou", "last":"Ambrose" }
        ]
        setTimeout(() => callback(res), 200)
    }

    render() {
        const { initLoading, list } = this.state
        return (
            <div >
                <div className="user-search"><InputSearch /></div>

                <div className="mail-list" style={{ padding: "0 12px 16px" }}>
                    <List className="demo-loadmore-list" loading={initLoading} itemLayout="horizontal"
                          split={false} dataSource={list} renderItem={item =>
                        (
                            <List.Item>
                                <Skeleton avatar title={false} loading={item.loading} active>
                                    <List.Item.Meta
                                        avatar={
                                            <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                                        }
                                        title={<a href="">{item.last}</a>}
                                        description="你好啊"
                                    />
                                    <div className="timeDate">02-24</div>
                                </Skeleton>
                            </List.Item>
                        )}
                    />
                </div>
            </div>
        )
    }
}
