// 存储当前登录用户信息
import {CHANGE_RIGHT_TYPE, INIT_CHAT_INFO, INIT_FRIEND_INFO, RECEIVE_CHAT_MSG} from "./action-type";

export const initUser = {
    user_id: 1,
    nickname: 'Mr.bo',
    phone: '188***8888',
    area: '江苏 淮安',
    autograph: '一位热爱编程的小鲜肉 😂',
    avatar: 'http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134'
}

// 存储聊天信息等
export const initChatInfo = {
    loading: true,
    messList: [],
    chatUserInfo: {},
    showRightType: 'welcome', /* 右侧显示内容 */
}

// 存储当前点击的用户信息
export const friendInfo = {}

// 同步 Action
export const initChatData   = chat => ({ type: INIT_CHAT_INFO, data: chat })
export const receiveChatMsg = chat => ({ type: RECEIVE_CHAT_MSG, data: chat })
export const showRightType  = chat => ({ type: CHANGE_RIGHT_TYPE, data: chat })
export const showFriendInfo = friend => ({ type: INIT_FRIEND_INFO, data: friend })
// 用户相关
const authSuccess = (user) => ({ type: AUTH_SUCCESS, data: user })
