/**
 * 用户信息组件，接收用户信息
 */
import React, { Component } from 'react'
import { Avatar, Row, Col, Button, Modal } from 'antd'

import './user-info.less'

export default class UserInfo extends Component {



    render() {
        return (
            <div>
                <div className="basic-info border-b">
                    <div className="left">
                        <Avatar size={60} shape="square" src="http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134" />
                    </div>
                    <div className="right">
                        <div className="user-name">Mr.bo 天下第一帅</div>
                        <div className="user-desc">手机号：183***8888</div>
                    </div>
                    <div className="clearfix" />
                </div>
                <Row className="user-item border-b">
                    <Col span={4}>地区</Col>
                    <Col span={20} style={{ textAlign: "right" }}>江苏 淮安</Col>
                </Row>
                <Row className="user-item border-b">
                    <Col span={4}>昵称</Col>
                    <Col span={20} style={{ textAlign: "right" }}>Mr.bo</Col>
                </Row>
                <Row className="user-item border-b">
                    <Col span={4}>手机号</Col>
                    <Col span={20} style={{ textAlign: "right" }}>183***8888</Col>
                </Row>
                <Row className="user-item border-b">
                    <Col span={4}>个性签名</Col>
                    <Col span={20} style={{ textAlign: "right" }}>我就是帅，你能有什么办法呢</Col>
                </Row>
                <div className="btn-operation">
                    <Button type="danger" block={true} onClick={() => this.logout()}>退出登录</Button>
                    <Button block={true}>注销账号</Button>
                </div>
            </div>
        )
    }

    logout = () => {
        Modal.confirm({
            title: 'Do you Want to delete these items?',
            content: 'Some descriptions',
            onOk() {
                console.log('OK');
            },
            onCancel() {
                console.log('Cancel');
            },
        });
    }
}
