/**
 * 对应 reducers 中的 action 同步/异步请求
 */
import Cookies from 'js-cookie'
import {websocket} from '../utils/websocket'
import {
    initChatData, receiveChatMsg, sendChatMsg, showRightType, showFriendInfo, authSuccess, errMsg, userInfo,
    logOut, modifyUserContacts, getNewFriends, getMailList
} from './init'
import {
    reqLogin, reqUserInfo, reqLogout, reqUserChatDataInit, reqNewFriendList,
    reqUserMailList
} from '../api'
import {pySegSort} from '../utils'

// 用户登录
export const login = (username, password) => {
    return async dispatch => {
        const response = await reqLogin({username, password})
        const result = response.data
        if (result.code === 200) {
            Cookies.set('token', result.data.token)
            dispatch(authSuccess(result.data))
        } else {
            dispatch(errMsg(result.msg))
        }
    }
}

// 退出登录
export const logout = () => {
    return async dispatch => {
        const response = await reqLogout()
        const result = response.data
        if (result.code === 200) {
            Cookies.remove('token')
            dispatch(logOut({}))
        } else {
            dispatch(errMsg(result.msg))
        }
    }
}

// 界面信息初始化
export const initMain = token => {
    return async dispatch => {
        const response = await reqUserChatDataInit(token)
        const result = response.data
        if (result.code === 200) {
            // 返回值得到的是 用户信息以及历史会话
            const {user, contact} = result.data
            dispatch(userInfo({userInfo: user, contacts: contact}))
            websocket(user)
        } else {
            dispatch(errMsg(result.msg))
        }
    }
}

// 获取用户信息
export const initUserInfo = token => {
    return async dispatch => {
        const response = await reqUserInfo(token)
        const result = response.data
        if (result.code === 200) {
            dispatch(userInfo(result.data))
            websocket(result.data.userInfo)
        } else {
            dispatch(errMsg(result.msg))
        }
    }
}

// 修改消息会话列表
export const modifyContacts = contacts => {
    return dispatch => {
        dispatch(modifyUserContacts({contacts}))
    }
}

// 初始化消息列表
export const initChatInfo = chatUserInfo => {
    return dispatch => {

        dispatch(initChatData({chatUserInfo, messList: [], loading: false, showRightType: 'message'}))
    }
}

// 切换右侧显示状态
export const changeRightType = type => {
    return dispatch => {
        dispatch(showRightType({showRightType: type}))
    }
}

// 发送消息
export const pushChatMsg = chat => {
    return dispatch => {
        dispatch(sendChatMsg(chat))
    }
}

// 收到好友消息
export const recvChatMsg = chat => {
    return dispatch => {
        dispatch(receiveChatMsg(chat))
    }
}

// 获取好友基本信息
export const getFriendInfo = friend_uid => {
    return async dispatch => {
        await dispatch(changeRightType('userInfo'))
        await dispatch(showFriendInfo(friend_uid))
    }
}

// 获取好友申请列表
export const getNewFriendList = token => {
    return async dispatch => {
        const response = await reqNewFriendList({token})
        const result = response.data
        if (result.code === 200) {
            dispatch(getNewFriends(result.data.list))
        }
    }
}

// 获取用户通讯录列表
export const getUserMailList = token => {
    return async dispatch => {
        const response = await reqUserMailList({token})
        const result = response.data
        if (result.code === 200) {
            const mailList = pySegSort(result.data.list)
            dispatch(getMailList(mailList))
        }
    }
}
