import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Avatar, Icon, Menu, Layout } from 'antd'
import { Link, withRouter } from 'react-router-dom'

import menuList from '../../config/menuConfig'
const { Sider } = Layout

class LeftNav extends Component {

    componentWillMount() {
        this.menuDoms = this.getMenuDom()
    }

    getMenuDom = () => {
        console.log(11)
        return menuList.map(item => (
            <Menu.Item key={item.path} title={item.title}>
                <Link to={item.path}><Icon type={item.icon} /></Link>
            </Menu.Item>
        ))
    }

    render() {
        const path = this.props.location.pathname
        const { user } = this.props
        return (
            <Sider className="left-nav" defaultCollapsed="true" collapsedWidth="68">
                <h2 className="user-avatar">
                    <Avatar shape="square" size="large" icon="user" src={user.avatar} />
                </h2>
                <div className="menus">
                    <Menu theme="dark" mode="inline" selectedKeys={[path]}>
                        { this.menuDoms }
                    </Menu>
                </div>
                <div className="setting">
                    <Icon type="setting" theme="filled" />
                </div>
            </Sider>
        )
    }
}

export default connect(
    state => ({ user: state.user }),
    {}
)(withRouter(LeftNav))
