import React, { Component } from 'react'
import { connect } from 'react-redux'
import Cookies from 'js-cookie'
import { Link, withRouter } from 'react-router-dom'
import { Avatar, Icon, Menu, Layout } from 'antd'

import menuList from '../../config/menuConfig'
const { Sider } = Layout

class LeftNav extends Component {

    state = { screenType: 'fullscreen' }

    componentWillMount() {
        this.menuDoms = this.getMenuDom()
    }

    componentDidMount() {
        const screenType = Cookies.get('screenType')
        this.setState({ screenType: screenType ? screenType : 'fullscreen' })
    }

    getMenuDom = () => {
        return menuList.map(item => (
            <Menu.Item key={item.path} title={item.title}>
                <Link to={item.path}><Icon type={item.icon} /></Link>
            </Menu.Item>
        ))
    }

    toSetting = () => {
        this.props.history.push({pathname: '/setting'})
    }

    setFullScreen = () => {
        const typeName = this.state.screenType === 'fullscreen' ? 'fullscreen-exit' : 'fullscreen'
        Cookies.set('screenType', typeName)
        this.setState({ screenType: typeName })
    }

    render() {
        const path = this.props.location.pathname
        const { user } = this.props
        return (
            <Sider className="left-nav" defaultCollapsed="true" collapsedWidth="68">
                <h2 className="user-avatar" onClick={this.toSetting}>
                    <Avatar shape="square" size="large" icon="user" src={user.avatar} />
                </h2>
                <div className="menus">
                    <Menu theme="dark" mode="inline" selectedKeys={[path]}>
                        { this.menuDoms }
                    </Menu>
                </div>
                {/* 全屏模式 */}
                <div className="setting" style={{ bottom: "80px"}} onClick={this.setFullScreen}>
                    <Icon type={this.state.screenType} />
                </div>
                <div className={['setting', path === '/setting' ? 'active' : null].join(' ')}
                     onClick={this.toSetting}
                >
                    <Icon type="setting" theme="filled" />
                </div>
            </Sider>
        )
    }
}

export default connect(
    state => ({ user: state.user }),
    {}
)(withRouter(LeftNav))
