import React, { Component } from 'react'
import { List, Skeleton, Avatar, Button } from 'antd'
import InputSearch from '../search'

const count = 3
export default class Contacts extends Component {

    state = {
        initLoading: true,
        loading: false,
        data: [],
        list: [],
    }

    componentDidMount() {
        this.getData(res => {
            console.log(res)
            this.setState({
                initLoading: false,
                data: res,
                list: res,
            });
        });
    }

    getData = callback => {
        setTimeout(() => callback([{
            name: "123",
            content: "heheh"
        }, {
            name: "123",
            content: "heheh"
        }]), 2000)
    }

    render() {
        const { initLoading, loading, list } = this.state
        const loadMore = !initLoading && !loading ? (
            <div style={{textAlign: 'center', marginTop: 12, height: 32, lineHeight: '32px',}}>
                <Button>添加好友</Button>
            </div>
        ) : null
        return (
            <div style={{ padding: "14px 12px 16px" }}>
                <div className="user-search">
                    <InputSearch />
                </div>
                <div className="mail-list">
                    <List className="demo-loadmore-list" loading={initLoading} itemLayout="horizontal"
                        dataSource={list} loadMore={loadMore} renderItem={item =>
                        (
                            <List.Item>
                                <Skeleton avatar title="" loading="false" active>
                                    <List.Item.Meta
                                        avatar={
                                            <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                                        }
                                        title={<a href="https://ant.design">asdsa</a>}
                                        description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                                    />
                                    <div>content</div>
                                </Skeleton>
                            </List.Item>
                        )}
                    />
                </div>
            </div>
        )
    }
}
