/**
 * 用户信息组件，接收用户信息
 */
import React, { Component } from 'react'
import { Avatar, Row, Col, Button, Modal, message as AM } from 'antd'

import './user-info.less'

export default class UserInfo extends Component {

    componentWillMount() {
        this.itemListDom = this.getItemList()
    }

    render() {
        return (
            <div>
                <div className="basic-info border-b">
                    <div className="left">
                        <Avatar size={60} shape="square" src="http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134" />
                    </div>
                    <div className="right">
                        <div className="user-name">Mr.bo 天下第一帅</div>
                        <div className="user-desc">手机号：183***8888</div>
                    </div>
                    <div className="clearfix" />
                </div>
                { this.itemListDom }
                <div className="btn-operation">
                    <Button type="danger" block={true} onClick={() => this.logout()}>退出登录</Button>
                    <Button block={true}>注销账号</Button>
                </div>
            </div>
        )
    }

    logout = () => {
        Modal.confirm({
            title: '确定退出当前帐号吗？退出后无法接收在线消息哦！',
            content: '聊天记录不受影响，再次上线会自动推送离线消息',
            cancelText: '取消',
            okText: '确定',
            onOk() {
                AM.success("点击了确认")
            },
            onCancel() {
                AM.error("点击了取消")
            },
        });
    }

    getItemList = () => {
        const itemList = [
            { itemName: "地区", itemValue: "江苏 淮安" },
            { itemName: "昵称", itemValue: "Mr.bo" },
            { itemName: "手机号", itemValue: "183***8888" },
            { itemName: "个性签名", itemValue: "我就是帅，你能有什么办法呢" },
        ]
        return itemList.map((item, index) => (
            <Row className="user-item border-b" key={index}>
                <Col span={4}>{item.itemName}</Col>
                <Col span={20} style={{ textAlign: "right" }}>{item.itemValue}</Col>
            </Row>
        ))
    }
}
