/**
 * 聊天应用骨架
 */
import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Switch, Route, Redirect } from 'react-router-dom'
import { Layout } from 'antd'
import Cookies from 'js-cookie'

import {initMain} from '../redux/actions'

import LeftNav from '../components/left-nav'
import Routers from '../router'
import {pySegSort} from '../utils'

class App extends Component {

    state = {screenType: null}

    componentWillMount() {
        const screenType = Cookies.get('screenType')
        this.setState({screenType})


        var pySegSort1 = pySegSort([
            {
                "area": "北京-北京",
                "autograph": "不是每个人都能成为自己想要的样子，但每个人，都可以努力成为自己想要的样子.",
                "avatar": "https://dss3.bdstatic.com/70cFv8Sh_Q1YnxGkpoWK1HF6hhy/it/u=2714458446,426719835&fm=26&gp=0.jpg",
                "nickname": "赵露思",
                "phone": 18899888899,
                "remark": "",
                "status": 1,
                "time": "2020年前",
                "user_id": 6
            }
        ]);console.log(pySegSort1)

    }

    componentDidMount() {
        const token = Cookies.get('token')
        if (token) {
            this.props.initMain(token)
        }
    }

    render() {
        const token = Cookies.get('token')
        const {redirectTo} = this.props.user
        if (!token || redirectTo === '/login') return <Redirect to='/login' />
        return (
            <Layout className={['container', this.state.screenType === 'fullscreen-exit' ? 'mini-pattern' : null].join(' ')}>
                <LeftNav />
                <Switch>
                    { Routers.map(item => <Route exact path={item.path} component={item.components} key={item.path} />) }
                    <Redirect to='/' />
                </Switch>
            </Layout>
        )
    }
}

export default connect(
    state => ({ user: state.user }), {initMain}
)(App)
