/**
 * 对应 reducers 中的 action 同步/异步请求
 */
import { INIT_CHAT_INFO } from './action-type'

const chatData = (chat) => ({ type: INIT_CHAT_INFO, data: chat })

export const initChatInfo = () => {

    return dispatch => {
        dispatch(chatData({
            showChatUid: 1
        }))
    }
}
