import { message as AM, notification } from 'antd'

const socket = new WebSocket("ws://192.168.8.100:8008/im")
let websocket = user => {

    socket.onopen = () => {
        socket.send(JSON.stringify({
            "type": "login",
            "uuid": user.id,
            "content": "Hello Go WebSocket",
            "username": user.nickname
        }))
    }

    socket.onmessage = msg => {
        const data = JSON.parse(msg.data)
        const { username, content, touuid } = data
        switch (data.type) {
            case "init":
                break;
            case "login":
                openNotificationWithIcon('success', `${data.username} 已连接`)
                break;
            case "message":
                AM.success(`message：${username}: ${content}`)
                break;
            case "private":
                if(touuid === user.user_id) {
                    AM.success(`private：${username}: ${content}`)
                }
                break;
            case "logout":
                AM.success(data.uuid + "已下线")
                break;
        }
    }

    socket.onclose = event => {
        console.log("Socket Closed Connection: ", event)
    }

    socket.onerror = error => {
        console.log("Socket Error: ", error)
    }
}

const sendMsg = msg => {
    console.log("sending msg: ", msg)
    socket.send(msg)
}

const openNotificationWithIcon = (type, content) => {
    notification[type]({
        message: 'Notification Title',
        description: content,
    });
}

export { websocket, sendMsg }
