import React, { Component } from 'react'

// 批量载入图片
const requireContext = require.context("../../assets/emotion", true, /^\.\/.*\.png$/);
const images = requireContext.keys().map(requireContext);

/**
 * 😀😁😂😃😄😅😆😉😊😋😎😍😘😗😙😚☺😇😐😑😶😏😣😥😮😯😪😫😴😌😛😜😝😒😓😔😕😲😷😖😞😟😤😢😭😦😧😨😬😰😱😳😵😡😠
 */

class Face extends Component {
    render() {
        const { showFace } = this.props
        return <div className="face-emjoy" style={{ display: showFace ? 'block' : 'none' }}>
            { images.map(item => <img src={item} alt="" />) }
            <b style={{ fontSize: 20 }}>🌹</b>
        </div>
    }
}

export default Face
