import React, { Component } from 'react'
import { List, Skeleton, Avatar } from 'antd'
import InputSearch from '../search'

export default class Contacts extends Component {

    state = {
        initLoading: true,
        loading: false,
        list: [],
    }

    componentDidMount() {
        this.getData(res => {
            this.setState({
                initLoading: false,
                list: res
            })
        })
    }

    getData = callback => {
        const res = [
            { "name": "马云", "avatar": "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778785114&di=4bf0a9cbe00cd200465af9fe46b3091e&imgtype=0&src=http%3A%2F%2Fwww.56ec.org.cn%2Fd%2Ffile%2Fnews%2Frwgd%2F2017-05-15%2Fa26c40ec7d83c66d78ad6f791952a01b.jpg", "last":"明天 9:00 准时到哦" },
            { "name": "王健林", "avatar": "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778837077&di=f569a7c9412eae7b1068ee89ccc9aa9f&imgtype=0&src=http%3A%2F%2Fimg.mp.itc.cn%2Fupload%2F20170709%2Fc266207e9ec74666b4dee5f74c634ae1_th.jpg", "last":"什么？这是什么意思" },
            { "name": "马化腾", "avatar": "https://ss0.bdstatic.com/70cFuHSh_Q1YnxGkpoWK1HF6hhy/it/u=1448222705,364131329&fm=26&gp=0.jpg", "last":"好吧，果然是你的风格" },
            { "name": "巴菲特", "avatar": "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778875578&di=e01fad34bb5d118d8f2a3b4d330b0482&imgtype=0&src=http%3A%2F%2Fimg.mp.itc.cn%2Fupload%2F20170518%2F4fbdc7b308224171b7eb127ef241d211_th.jpg", "last":"明天一起吃饭啊" },
            { "name": "刘亦菲", "avatar": "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778899824&di=86416b5c424eec83f25460f53223cded&imgtype=0&src=http%3A%2F%2Fwx3.sinaimg.cn%2Fbmiddle%2F69949546ly1gaxecjgu3gj20u00mi495.jpg", "last":"今晚去找你" },
            { "name": "刘强东", "avatar": "Veeti", "last":"妈的，又被告了" },
            { "name": "李彦宏", "avatar": "Marilou", "last":"百度AI人工智能，造福人类" }
        ]
        setTimeout(() => callback(res), 200)
    }

    render() {
        const { initLoading, list } = this.state
        return (
            <div >
                <div className="user-search"><InputSearch /></div>

                <div className="mail-list" style={{ padding: "0 12px 16px" }}>
                    <List className="demo-loadmore-list" loading={initLoading} itemLayout="horizontal"
                          split={false} dataSource={list} renderItem={item =>
                        (
                            <List.Item>
                                <Skeleton avatar title={false} loading={item.loading} active>
                                    <List.Item.Meta
                                        avatar={
                                            <Avatar src={item.avatar} />
                                        }
                                        title={<a href="">{item.name}</a>}
                                        description={item.last}
                                    />
                                    <div className="timeDate">02-24</div>
                                </Skeleton>
                            </List.Item>
                        )}
                    />
                </div>
            </div>
        )
    }
}
