import React, { Component } from 'react'
import { connect } from 'react-redux'

import { Avatar, Row, Col, Icon } from 'antd'

class MessHeader extends Component {
    render() {
        return (
            <div>
                <Row>
                    <Col span={20}>
                        <Avatar shape="square" size={36} src="https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778785114&di=4bf0a9cbe00cd200465af9fe46b3091e&imgtype=0&src=http%3A%2F%2Fwww.56ec.org.cn%2Fd%2Ffile%2Fnews%2Frwgd%2F2017-05-15%2Fa26c40ec7d83c66d78ad6f791952a01b.jpg" />
                        <span style={{ fontSize: 18, marginLeft: 10, color: "#000" }}>马云</span>
                    </Col>
                    <Col span={4}>
                        <div style={{ textAlign: "right" }}>
                            <Icon type="more" rotate="90" className="icon-more" />
                        </div>
                    </Col>
                </Row>
            </div>
        )
    }
}

export default connect(
    state => ({ chat: state.chat }),
    {}
)(MessHeader)
