import React, { Component } from 'react'
import { connect } from 'react-redux'
import { withRouter } from 'react-router-dom'

import { Avatar, Row, Col, Icon, message } from 'antd'

class MessHeader extends Component {

    state = {  }

    render() {
        const { chat: { chatUserInfo: { avatar, name } }} = this.props
        return (
            <div>
                <Row>
                    <Col span={20}>
                        <Avatar shape="square" size={36} src={avatar} />
                        <span style={{ fontSize: 18, marginLeft: 10, color: "#000" }}>{name}</span>
                    </Col>
                    <Col span={4}>
                        <div style={{ textAlign: "right" }}>
                            <Icon type="more" rotate="90" className="icon-more" onClick={this.handleClick} />
                        </div>
                    </Col>
                </Row>
            </div>
        )
    }

    handleClick = () => {
        message.info("好友信息展示页面，怎么显示好呢")
        this.props.history.push('/')
    }
}

export default connect(
    state => ({ chat: state.chat }), {}
)(MessHeader)
