/**
 * 聊天应用骨架
 */
import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Switch, Route, Redirect } from 'react-router-dom'
import { Layout } from 'antd'
import Cookies from 'js-cookie'
import { websocket } from '../utils/websocket'

import LeftNav from '../components/left-nav'
import Routers from '../router'

class App extends Component {

    state = { token: null }

    componentDidMount() {
        const token = Cookies.get('token')
        if (token) {
            this.setState({token})
            const { user } = this.props
            // 正确操作是先获取用户信息，成功后连接 socket
            websocket(user)
        }
    }

    render() {
        // 检测是否登录，判断 cookie 中是否存在 token
        if (!this.state.token) {
            return <Redirect to='/login' />
        }
        const screenType = Cookies.get('screenType')
        return (
            <Layout className={['container', screenType === 'fullscreen-exit' ? 'mini-pattern' : null].join(' ')}>
                <LeftNav />
                <Switch>
                    { Routers.map(item => <Route exact path={item.path} component={item.components} key={item.path} />) }
                    <Redirect to='/' />
                </Switch>
            </Layout>
        )
    }
}

export default connect(
    state => ({ user: state.user }), {}
)(App)
