import React, { Component } from 'react'
import { List, Skeleton, Avatar, Button } from 'antd'
import InputSearch from '../search'

const count = 3
export default class Contacts extends Component {

    state = {
        initLoading: true,
        loading: false,
        data: [],
        list: [],
    }

    componentDidMount() {
        this.getData(res => {
            console.log(res)
            this.setState({
                initLoading: false,
                data: res,
                list: res,
            });
        });
    }

    getData = callback => {
        const res = [
            {"gender":"male","name":{"title":"Mr","first":"Tristan","last":"Roy"},"email":"tristan.roy@example.com","nat":"CA"},
            {"gender":"male","name":{"title":"Mr","first":"Veeti","last":"Ahonen"},"email":"veeti.ahonen@example.com","nat":"FI"},
            {"gender":"female","name":{"title":"Mrs","first":"Marilou","last":"Ambrose"},"email":"marilou.ambrose@example.com","nat":"CA"}
        ]
        setTimeout(() => callback(res), 2000)
    }

    render() {
        const { initLoading, loading, list } = this.state
        const loadMore = !initLoading && !loading ? (
            <div style={{textAlign: 'center', marginTop: 12, height: 32, lineHeight: '32px',}}>
                <Button>添加好友</Button>
            </div>
        ) : null
        return (
            <div >
                <div className="user-search">
                    <InputSearch />
                </div>
                <div className="mail-list" style={{ padding: "0 12px 16px" }}>
                    <List className="demo-loadmore-list" loading={initLoading} itemLayout="horizontal"
                        dataSource={list} loadMore={loadMore} renderItem={item =>
                        (
                            <List.Item>
                                <Skeleton avatar title={false} loading={item.loading} active>
                                    <List.Item.Meta
                                        avatar={
                                            <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                                        }
                                        title={<a href="https://ant.design">{item.name.last}</a>}
                                        description="你好啊"
                                    />
                                    <div></div>
                                </Skeleton>
                            </List.Item>
                        )}
                    />
                </div>
            </div>
        )
    }
}
