    state = {
        loading: true,
        list: []
    }

    componentDidMount() {
        this.getMessList((messList) => {
            setTimeout(() => {
                this.setState({
                    loading: false,
                    list: messList
                })
                window.scrollTo(0, this.refs.messList.h)
            }, 1000)
        })
    }

    componentUpdateMount() {
        this.testRefEle.current.scrollTop = this.testRefEle.current.scrollHeight;

    }

    getMessList = (callback) => {
        const messList = [
            {id: 1, avatar: "https://timgsa.baidu.com/timg?image&quality=80&size=b9999_10000&sec=1582778785114&di=4bf0a9cbe00cd200465af9fe46b3091e&imgtype=0&src=http%3A%2F%2Fwww.56ec.org.cn%2Fd%2Ffile%2Fnews%2Frwgd%2F2017-05-15%2Fa26c40ec7d83c66d78ad6f791952a01b.jpg", message: "下个月花呗不用还了，我还要另外支付你一个亿随便你花", position: "left"},
