import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Layout } from 'antd'
import PubSub from 'pubsub-js'

import Message from './message'
import MessHeader from './header'
import ChatTextarea from './textarea'
import UserInfo from '../../mail-list/user'
import Welcome from '../../../components/welcome'

const { Header, Content, Footer } = Layout
class Chat extends Component {

    state = { user: null }

    componentDidMount() {
        PubSub.subscribe('showUserInfo', (msg, user) => this.setState({ user }))
    }

    render() {
        const { chat: { showRightType } } = this.props
        return showRightType === 'welcome'  ? <Welcome />  : (
               showRightType === 'userInfo' ? <UserInfo user={user} /> : 
            (
                <div style={{ maxHeight: "100%", minHeight: "100%" }}>
                    <Header className="message-header"><MessHeader /></Header>
                    <Content className="message-body"><Message /></Content>
                    <Footer className="message-footer"><ChatTextarea /></Footer>
                </div>
            )
        )
    }
}

export default connect(
    state => ({ chat: state.chat }), {}
)(Chat)
