/*
包含了n个接口请求的函数的模块
函数返回值为: promise
 */
import ajax from './ajax'

// 登陆接口
export const reqLogin = ({ username, password }) => ajax('/login', { username, password })

// 获取用户信息
export const reqUserInfo = token => ajax('/userInfo', { token })

// 获取群聊列表
export const reqGroupList = token => ajax('/group', { token })
