import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { connect } from 'react-redux'
import {Avatar, List} from 'antd'

class NewFriend extends Component {

    render() {
        return (
            <div className="m-user-info">
                <div className="header">好友申请</div>
                <List
                    className="m-list-user"
                    itemLayout="horizontal"
                    split={false}
                    renderItem={user => (
                        <List.Item
                            onClick={() => this.showUserInfo(user.uid)}
                        >
                            <List.Item.Meta
                                avatar={<Avatar shape="square" size="large" src={user.avatar}/>}
                                title={`${user.remark} (${user.name})`}
                            />
                        </List.Item>
                    )}
                />
            </div>
        )
    }
}

export default connect(
    state => ({friend: state.friend, chat: state.chat}), {}
)(withRouter(NewFriend))
