import React, { Component } from 'react'
import { List, Avatar } from 'antd'

import InputSearch from './search'
import mailList from '../../config/mailListConfig'

class MailList extends Component {

    componentWillMount() {
        this.mailListDom = this.createMailListDom()
    }

    render() {
        return (
            <div style={{ height: "100%" }}>
                <div className="user-search"><InputSearch /></div>
                <div className="mail-list">{ this.mailListDom }</div>
            </div>
        )
    }

    createMailListDom = () => {
        return Object.keys(mailList).map(key => (
            <div className="m-list-item" key={key}>
                <div className="m-list-title">{key}</div>
                <List
                    className="m-list-user text-ellipsis"
                    itemLayout="horizontal"
                    dataSource={mailList[key]}
                    split={false}
                    renderItem={user => (
                        <List.Item>
                            <List.Item.Meta avatar={<Avatar shape="square" size="large"  src={ user.avatar } />} 
                            title={`${user.name} (${user.remark})`} />
                        </List.Item>
                    )}
                />
            </div>
        ))
    }
}

export default MailList
