import { combineReducers } from 'redux'

import {
    INIT_CHAT_INFO, INIT_MESS_LIST, RECEIVE_CHAT_MSG,
    CHANGE_RIGHT_TYPE
} from './action-type'

// 当前登录的用户信息
const initUser = {
    user_id: 1,
    nickname: 'Mr.bo',
    avatar: 'http://t8.baidu.com/it/u=3571592872,3353494284&fm=79&app=86&size=h300&n=0&g=4n&f=jpeg?sec=1583315718&t=9caf2bcbcf5ea34c543c426750bb1134'
}
function user(state = initUser, action) {
    return state
}

// 当前查看的消息列表
const initChatInfo = {
    loading: true,
    messList: [],
    chatUserInfo: {},
    showRightType: 'welcome', /* 右侧显示内容 */
}
function chat(state = initChatInfo, action) {
    switch (action.type) {
        case INIT_CHAT_INFO:
            return {...state, ...action.data}
        case INIT_MESS_LIST:
            return {...state, ...action.data}
        case RECEIVE_CHAT_MSG: /* 接收消息 */
            const chatMsg = action.data
            return {
                chatUserInfo: state.chatUserInfo,
                messList: [...state.messList, chatMsg],
            }
        case CHANGE_RIGHT_TYPE:
            return {...state, ...action.data}
        default:
            return state
    }
}

export default combineReducers({
    user, chat
})
